mapper.update:
UPDATE t_user
SET user_type={userType}
AND name={name}
AND code={code}
AND faculty_id={facultyId}
AND phone={phone}
AND mail={mail}
where user_id={userId}
AND is_delect=0

mapper.select:
SELECT * FROM t_user where user_id IN
#{item}
AND is_delect=0

mapper.update:
UPDATE t_user
SET is_delect=1
WHERE user_id=#{userId}
